import React, { useState, useEffect } from 'react'
import Navbar from './Navbar'
import { MDBContainer,MDBRow,MDBCol,MDBCard,MDBCardBody,MDBIcon,MDBCardHeader,MDBCardTitle } from "mdbreact";
import Multiselect from "react-multi-select-component";
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

const programminglabels = [
    { label: 'Array', value: '1' }, { label: 'String', value: '2' }, { label: 'DP', value: '3' }, { label: 'Binary Search', value: '4' }, { label: 'Sort', value: '5' },
    { label: 'Greedy', value: '6' }, { label: 'Two Pointers', value: '7' }, { label: 'Slvalueing Window', value: '8' }, { label: 'Stack', value: '9' },
    { label: 'Queue', value: '10' }, { label: 'Heap', value: '11' }, { label: 'Priority Queue', value: '12' }, { label: 'Tree', value: '13' }, 
    { label: 'Linked List', value: '14' }, { label: 'Recursion', value: '15' }, { label: 'Hashing', value: '16' }, { label: 'BST', value: '17' },
    { label: 'Backtracking', value: '18' }, { label: 'DFS, BFS', value: '19' }, { label: 'Graph', value: '20' }, { label: 'Union Find', value: '21' },
    { label: 'Divvaluee and Conquer', value: '22' }, { label: 'Shortest Path', value: '23' }, { label: 'Topological Sort', value: '24' }, { label: 'Trie', value: '25' }
]

const CSFundamentals = [
    { label: 'Data Structures', value: '1' }, { label: 'Algorithms', value: '2' }, { label: 'Object Oriented Programming', value: '3' }, 
    { label: 'Operating System', value: '4' }, { label: 'Database Management System', value: '5' }, { label: 'Computer Networks', value: '6' }, { label: 'Computer Architecture', value: '7' }, { label: 'Theory of Computation', value: '8' }, { label: 'Compiler Design', value: '9' }
]

function InterviewExperience() {

    const [programmingTopics, setProgrammingTopics] = useState([])
    const [csFundamentals, setCSFundamentals] = useState([]);

    useEffect(() => {
        programmingTopics.forEach((topic) => {
            console.log(topic.label)
        })
    }, [ programmingTopics, csFundamentals ])

    return (
        <div>
        <Navbar />
            <MDBContainer className="mb-3 mt-5">
            <MDBRow>
                <MDBCol md="12">
                    <MDBCard>
                        <MDBCardHeader color="primary-color" tag="h3">
                            <MDBCardTitle className="text-center mt-2">Add Interview Experience</MDBCardTitle>
                        </MDBCardHeader>
                        <MDBCardBody>
                            <form>
                                <h6 class="heading-small text-muted mb-4">Company Information</h6>
                                <div class="pl-lg-4">
                                    <div class="row">
                                        <div class="col-lg-6">
                                            <div class="form-group focused">
                                                <label class="form-control-label" for="input-company-name">Company Name</label>
                                                <input type="text" value="input-company-name" class="form-control form-control-alternative" placeholder="Enter the Company Name"></input>
                                            </div>
                                        </div>
                                        <div class="col-lg-6">
                                            <div class="form-group focused">
                                                <label class="form-control-label" for="input-role">Role</label>
                                                <input type="text" value="input-role" class="form-control form-control-alternative" placeholder="Enter the Role"></input>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <hr class="my-4 bg-primary"></hr>
                                <h6 class="heading-small text-muted mb-4">labels Covered</h6>
                                <div class="pl-lg-4">
                                    <div class="row">
                                        <div style={{zIndex:1001}} class="col-md-12">
                                            <Multiselect
                                                options={programminglabels}
                                                value={programmingTopics}
                                                onChange={setProgrammingTopics}
                                                labelledBy={"Select"}
                                            />
                                        </div>
                                        <div style={{zIndex:1000}} class="col-md-12 mt-5">
                                            <Multiselect
                                                options={CSFundamentals}
                                                value={csFundamentals}
                                                onChange={setCSFundamentals}
                                                labelledBy={"Select"}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <hr class="my-4 bg-primary"></hr>
                                <h6 class="heading-small text-muted mb-4">Other Details</h6>
                                <div class="pl-lg-4">
                                    <div class="row">
                                        <div class="col-md-12">
                                            <div class="form-group focused">
                                                <label class="form-control-label" for="input-job">Interview Experience</label>
                                                <CKEditor
                                                    editor={ ClassicEditor }
                                                    data="<p>Hello from CKEditor 5!</p>"
                                                    onReady={ editor => {
                                                        // You can store the "editor" and use when it is needed.
                                                        console.log( 'Editor is ready to use!', editor );
                                                    } }
                                                    onChange={ ( event, editor ) => {
                                                        const data = editor.getData();
                                                        console.log( { event, editor, data } );
                                                    } }
                                                    onBlur={ ( event, editor ) => {
                                                        console.log( 'Blur.', editor );
                                                    } }
                                                    onFocus={ ( event, editor ) => {
                                                        console.log( 'Focus.', editor );
                                                    } }
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <hr class="my-4 bg-primary"></hr>
                                <div className="text-center">
                                <button class="btn btn-primary" type="submit">Submit <MDBIcon far icon="paper-plane" className="ml-2" /></button>
                                </div>
                            </form>
                        </MDBCardBody>
                    </MDBCard>
                </MDBCol>
            </MDBRow>
        </MDBContainer>
        </div>
    )
}

export default InterviewExperience
