import React, { useEffect, useState } from 'react'
import Navbar from './Navbar'
import axios from 'axios'
import { BrowserRouter as Router, Link } from 'react-router-dom'
import { MDBCard,MDBCardHeader, MDBCardBody, MDBCardTitle, MDBCardText, MDBRow, MDBCol, MDBIcon, MDBContainer} from 'mdbreact';

const ColleagueProfile = (props) => {
    return (
    <MDBContainer>
        <MDBCard className="mb-3">
            <MDBCardHeader className="cyan darken-4">
                <MDBCardTitle className="text-white font-weight-bold mt-3"><img src="https://bootdey.com/img/Content/avatar/avatar7.png" alt="Admin" className="rounded-circle white mr-3" width="60"></img>        { props.details.name }</MDBCardTitle>
            </MDBCardHeader>
            <MDBCardBody className="grey lighten-4">
                {/* {props.details.company} */}
                {/* <MDBCardText className="mt-2"> */}
                    <MDBCol md='12' className='d-flex justify-content-center mt-2'>
                    <Link to={props.details.linkedIn} target="_blank" className='px-2 fa-lg li-ic'><MDBIcon fab icon='linkedin-in'></MDBIcon></Link>
                        <a href={props.details.linkedIn} target="_blank" className='px-2 fa-lg li-ic'>
                            <MDBIcon fab icon='linkedin-in'></MDBIcon>
                        </a>
                        <a href={props.details.instagram} className='px-2 fa-lg fb-ic'>
                            <MDBIcon fab icon='instagram'></MDBIcon>
                        </a>
                        <a href={props.details.twitter} className='px-2 fa-lg tw-ic'>
                            <MDBIcon fab icon='twitter'></MDBIcon>
                        </a>
                        <a href={props.details.facebook} className='px-2 fa-lg fb-ic'>
                            <MDBIcon fab icon='facebook-f'></MDBIcon>
                        </a>
                    </MDBCol>
                {/* </MDBCardText> */}
                <a href='!#' className='mt-1 d-flex justify-content-end align-items-center full_profile'>
                    <h5 className='text-info'>See profile{' '}
                        <MDBIcon icon='chevron-right' className='ml-2' size='sm'></MDBIcon>
                    </h5>
                </a>
            </MDBCardBody>
        </MDBCard>
    </MDBContainer>
    );
    };
function Colleague(){

    const [ profiles, setProfiles ] = useState([
        {
            name: '',
            instagram: '',
            facebook: '',
            twitter: '',
            linkedIn: ''
        }
    ])

    useEffect(() => {

        const fetchAllUsers = async () => {

            try {   

                const config = {
                    headers: {
                        'Content-Type': 'application/json',
                        'auth-token': localStorage.getItem('token')
                    }
                }

                axios.get('http://localhost:5000/profile', config)
                    .then((res) => {
                        //console.log(res)
                        const users = res.data.map((user) => {
                            return { instagram: user.instagram, twitter: user.twitter, facebook: user.facebook, linkedIn: user.linkedIn, name: user.userId.name }
                        })
                        setProfiles(users)
                    })
                    .catch((err) => {
                        console.log(err)
                    })

            } catch (error) {
                console.log(error)
            }

        }

        fetchAllUsers()

    }, [])

    return (
        <Router>
            <Navbar />
            <MDBContainer className="mt-5">
                <MDBRow>
                    { profiles.map((profile, index) => { 
                        return <ColleagueProfile key={index} details={{ 
                            name: profile.name || '',
                            linkedIn: profile.linkedIn || '',
                            instagram: profile.instagram || '',
                            twitter: profile.twitter || '',
                            facebook: profile.facebook || '',
                        }} /> })  
                    }
                    {/* <ColleagueProfile details={{name: '', company:"Deutsche Bank"}}></ColleagueProfile> */}
                    {/* <ColleagueProfile details={{name: '',company:"Google"}}></ColleagueProfile> */}
                    {/* <ColleagueProfile details={{name: '',company:"Microsoft"}}></ColleagueProfile> */}
                </MDBRow>
            </MDBContainer>
        </Router>
    );
}

export default Colleague;
