import React, { useEffect, useState } from "react";
import axios from 'axios'
import Avatar from '@material-ui/core/Avatar';
import { MDBContainer,MDBCol,MDBRow, MDBCard, MDBCardBody, MDBCardTitle, MDBCardText, MDBIcon } from "mdbreact";

function UserProfile() {

    const [ myProfile, setMyProfile ] = useState({})

    useEffect(() => {

        const fetchMyProfile = async () => {

            try {

                const config = {
                    headers: {
                        'Content-Type': 'application/json',
                        'auth-token': localStorage.getItem('token')
                    }
                }

                const res = await axios.get(`http://localhost:5000/profile/me`, config)
                console.log(res)
                setMyProfile(res)

            } catch (error) {

                console.log(error.response)

            }

        }

        fetchMyProfile()

    }, [])

    return(
        <MDBCol className="profile pr-5 pb-4 mt-5" lg="3">
            <MDBContainer fluid className="profile_container">
                <MDBRow className="mb-4">
                        <MDBCol className="text-center">
                            <MDBCardBody className="mt-5">
                            {/* /<Avatar /> */}
                            <Avatar src={myProfile.data ? `/images/${myProfile.data.avatar}` : ''} className="red" style={{}}>{myProfile.data ? myProfile.data.userId.name[0] : ''}</Avatar>
                                <MDBCardTitle className="text-info font-weight-bold mt-3">{myProfile.data ? myProfile.data.userId.name : ''}</MDBCardTitle>
                                <MDBCardText className="text-info">{myProfile.data ? myProfile.data.company : ''}</MDBCardText>
                            </MDBCardBody>
                        </MDBCol>
                </MDBRow>
            </MDBContainer>
        </MDBCol>
    )
}

export default UserProfile
