import React, { useState } from 'react';
import './App.css';
import Login from './components/login';
import Signup from './components/Signup';
import Dashboard from './components/Dashboard'
import {BrowserRouter as Router , Route} from 'react-router-dom';
import '@fortawesome/fontawesome-free/css/all.min.css'; 
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import 'mdbreact/dist/css/mdb.css';

function App() {

  const [ user, setUser ] = useState({
    token: '',
    isAuthenticated: false,
    isLoading: true
  })

  const onAuthenticated = (token, isAuthenticated, isLoading) => {
    setUser({
      token, isAuthenticated, isLoading
    })
    console.log(token)
  }

  return (
    <Router>
        <Route path="/" exact render={() => <Login onAuthenticated={onAuthenticated}/>}></Route>
        <Route path="/Signup" exact render={() => <Login onAuthenticated={onAuthenticated}/>}></Route>
        <Route path="/dashboard" exact component={Dashboard}></Route>
    </Router>
  );
}

export default App;
