import React, { useEffect, useState } from "react";
import PostItem from './PostItem'
import axios from 'axios'
import AddIcon from '@material-ui/icons/AddBox';
import Paper from '@material-ui/core/Paper';
import Tooltip from '@material-ui/core/Tooltip';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import { MDBCol, MDBCard, MDBIcon, MDBBtn, MDBRow } from 'mdbreact';



function Post() {

    const [ posts, setPosts ] = useState([])
    const [ suggestions, setSuggestions ] = useState([])
    const [ toggle, setToggle ] = useState(false)
    const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {

    setValue(newValue);

    if(newValue===0) {

        setSuggestions(posts)

    } else if(newValue===1) {

        const temp = posts.filter((post) => {
            if(!post.experience) {
                return post
            }
        })

        setSuggestions(temp)

    } else if(newValue===2) {

        const temp = posts.filter((post) => {
            if(post.experience) {
                return post
            }
        })

        setSuggestions(temp)

    }

  };

    useEffect(() => {

        const fetchPosts = async () => {

            try {

                const config = {
                    headers: {
                        'Content-Type': 'application/json',
                        'auth-token': localStorage.getItem('token')
                    }
                }

                const res1 = await axios.get('http://localhost:5000/interview', config)
                const res2 = await axios.get('http://localhost:5000/post', config)
                
                let posts = res1.data
                posts.unshift(...res2.data)
                // console.log(posts)
                posts.sort((post1, post2) => {
                    let x = new Date(post1.createdAt)
                    let y = new Date(post2.createdAt)
                    if(x<y) return 1
                    if(x>=y) return -1
                    return 0
                })
                // console.log(posts.data.profileId.avatar)
                setPosts(posts)
                // setSuggestions(posts)

            } catch (error) {

                console.log(error.response)

            }

        }

        fetchPosts()
        // console.log(posts.data.profileId.avatar)

    }, [ suggestions])

    return(
        <MDBCol className="post pb-4 mt-4" xl="8">
            <MDBCard className="mx-5 mdb-color">
            <Paper className="mdb-color lighten-5">
            <Tabs
                value={value}
                onChange={handleChange}
                indicatorColor="primary"
                textColor="primary"
                centered
            >
            <Tab label="All"  />
            <Tab label="Posts" icon={<MDBIcon icon="pen"/>}/>
            <Tab label="Interview Experiences" icon={<MDBIcon icon="chalkboard-teacher"/>}/>
            {/* <Tooltip title="Add Post"><AddIcon style={{fontSize: '40'}} onClick={e => setToggle(!toggle)} className="my-auto ml-5 add_post" /></Tooltip> */}
            </Tabs>
            </Paper>
            { 1 && <MDBRow className="mt-5 mx-5 mb-5">
                <MDBCol size="12"><MDBBtn className="w-100 mx-auto">Add Post</MDBBtn></MDBCol>
                <MDBCol size="12"><a href="/dashboard/interview-exp"><MDBBtn className="w-100 mx-auto" outline rounded>Add Interview Experience</MDBBtn></a></MDBCol>
            </MDBRow> }
            </MDBCard>
            
            {
                suggestions.length!==0 ? 
                suggestions.map((post) => {
                    return <PostItem  key={post._id} data={post} />
                }) : 
                posts.map((post) => {
                    return <PostItem  key={post._id} data={post} />
                })

            }
            {/* <PostItem />
            <PostItem />
            <PostItem />
            <PostItem />
            <PostItem />
            <PostItem />
            <PostItem /> */}
        </MDBCol>
    )
}

export default Post
