import React, { useState, useEffect } from 'react'
import PostItem from './PostItem'
import Navbar from './Navbar'
import axios from 'axios'
import { MDBCol, MDBCard, MDBIcon, MDBBtn, MDBRow, MDBContainer } from 'mdbreact';

function YourPosts() {

    const [ posts, setPosts ] = useState([])

    useEffect(() => {

        const fetchPosts = async () => {

            try {

                const config = {
                    headers: {
                        'Content-Type': 'application/json',
                        'auth-token': localStorage.getItem('token')
                    }
                }

                const res1 = await axios.get('http://localhost:5000/post/my', config)
                
                let posts = res1.data
                // console.log(posts)
                posts.sort((post1, post2) => {
                    let x = new Date(post1.createdAt)
                    let y = new Date(post2.createdAt)
                    if(x<y) return 1
                    if(x>=y) return -1
                    return 0
                })
                // console.log(posts.data.profileId.avatar)
                setPosts(posts)
                // setSuggestions(posts)

            } catch (error) {

                console.log(error.response)

            }


        }

        fetchPosts()

    }, [])

    return (
        <>
        <Navbar />
        <MDBContainer className="post pb-4 mt-4" xl="8">
            {
                posts.map((post) => {
                    return <PostItem  key={post._id} data={post} parent="myposts"/>
                })
            }
        </MDBContainer>
        </>
    )
}

export default YourPosts
