import React, { useEffect, useState } from 'react'
import Navbar from './Navbar'
import Searchbar from './Searchbar'
import axios from 'axios'
import { Link } from 'react-router-dom'
import { MDBCard,MDBCardHeader, MDBCardBody, MDBCardTitle, MDBRow, MDBCol, MDBIcon, MDBContainer} from 'mdbreact';

const ColleagueProfile = (props) => {

    return (

        <MDBContainer>
            <MDBCard className="mb-3">
                <MDBCardHeader className="cyan darken-4">
                    <MDBCardTitle className="text-white font-weight-bold mt-3"><img src="https://bootdey.com/img/Content/avatar/avatar7.png" alt="Admin" className="rounded-circle white mr-3" width="60"></img>        { props.details.name }</MDBCardTitle>
                </MDBCardHeader>
                <MDBCardBody className="grey lighten-4">
                    <MDBRow>
                        <MDBCol md="4">
                            <MDBRow><MDBCol className="font-weight-bold">Nirma University</MDBCol></MDBRow>
                        </MDBCol>
                        <MDBCol md="4">
                            { props.details.linkedIn && <a href={props.details.linkedIn} className='px-2 fa-lg li-ic'>
                                <MDBIcon fab icon='linkedin-in'></MDBIcon>
                            </a> }
                            { props.details.instagram && <a href={props.details.instagram} className='px-2 fa-lg fb-ic'>
                                <MDBIcon fab icon='instagram'></MDBIcon>
                            </a> }
                            { props.details.twitter && <a href={props.details.twitter} className='px-2 fa-lg tw-ic'>
                                <MDBIcon fab icon='twitter'></MDBIcon>
                            </a> }
                            { props.details.facebook && <a href={props.details.facebook} className='px-2 fa-lg fb-ic'>
                                    <MDBIcon fab icon='facebook-f'></MDBIcon>
                            </a> }
                        </MDBCol>
                        <MDBCol md="4">
                            <Link to={{ pathname: `/dashboard/profiles/${props.details.username}`, userId: props.details.userId }} className='mt-1 full_profile' >
                                <h5 className='text-info'>{'                '}See profile{' '}
                                    <MDBIcon icon='chevron-right' className='ml-2' size='sm'></MDBIcon>
                                </h5>
                            </Link>
                        </MDBCol>
                    </MDBRow>              
                </MDBCardBody>
            </MDBCard>
        </MDBContainer>

    );
};
function Colleague(){

    const [ profiles, setProfiles ] = useState([
        {
            name: '',
            username: '',
            instagram: '',
            facebook: '',
            twitter: '',
            linkedIn: '',
            college: '',
            batch: '',
            portfolio: '',
            company: '',
            userId: ''
        }
    ])

    useEffect(() => {

        const fetchAllUsers = async () => {

            try {   

                const config = {
                    headers: {
                        'Content-Type': 'application/json',
                        'auth-token': localStorage.getItem('token')
                    }
                }

                axios.get('http://localhost:5000/profile', config)
                    .then((res) => {
                        const users = res.data.map((user) => {
                            return { instagram: user.instagram, twitter: user.twitter, facebook: user.facebook, linkedIn: user.linkedIn, company: user.company, name: user.userId.name, username: user.userId.username, userId: user.userId._id }
                        })
                        setProfiles(users)
                    })
                    .catch((err) => {
                        console.log(err)
                    })

            } catch (error) {
                console.log(error)
            }

        }

        fetchAllUsers()

    }, [])

    return (
        <>
            <Navbar />
            <Searchbar />
            <MDBContainer className="mt-5">
                <MDBRow>
                    { profiles.map((profile, index) => { 
                        return <ColleagueProfile key={index} details={{ 
                            name: profile.name || '',
                            username: profile.username || '',
                            linkedIn: profile.linkedIn || '',
                            instagram: profile.instagram || '',
                            twitter: profile.twitter || '',
                            facebook: profile.facebook || '',
                            company: profile.company || '',
                            userId: profile.userId || ''
                        }} /> })  
                    }
                </MDBRow>
            </MDBContainer>
        </>
    );
}

export default Colleague;
