import React, { useState, useEffect, useRef } from 'react'
import PostItem from './PostItem'
import Navbar from './Navbar'
import { Alert, AlertTitle } from '@material-ui/lab';
import axios from 'axios'
import { MDBCol, MDBCard, MDBIcon, MDBBtn, MDBRow, MDBContainer } from 'mdbreact';

function YouInterviewExperiences() {

    const [ posts, setPosts ] = useState([])
    const [ flag, setFlag ] = useState(true)
    const check = useRef(true)

    useEffect(() => {

        console.log('Interview')

        const fetchPosts = async () => {

            try {

                const config = {
                    headers: {
                        'Content-Type': 'application/json',
                        'auth-token': localStorage.getItem('token')
                    }
                }

                const res1 = await axios.get('http://localhost:5000/interview/my', config)
                
                let posts = res1.data
                // console.log(posts)
                posts.sort((post1, post2) => {
                    let x = new Date(post1.createdAt)
                    let y = new Date(post2.createdAt)
                    if(x<y) return 1
                    if(x>=y) return -1
                    return 0
                })
                // console.log(posts.data.profileId.avatar)
                setPosts(posts)
                // setSuggestions(posts)

            } catch (error) {

                console.log(error.response)

            }


        }

        fetchPosts()

    },[])

    const onDelete = async (post_id) => {
        console.log(post_id)

        const Posts = posts.filter((post) => {
            return post._id!==post_id
        })

        setPosts(Posts)

        try {

            const config = {
                headers: {
                    'Content-Type': 'application/json',
                    'auth-token': localStorage.getItem('token')
                }
            }
            
            const res1 = await axios.delete(`http://localhost:5000/interview/my/${post_id}`, config)
            
            console.log(res1)

        } catch (error) {

            console.log(error.response)

        }

        // try {

        //     const config = {
        //         headers: {
        //             'Content-Type': 'application/json',
        //             'auth-token': localStorage.getItem('token')
        //         }
        //     }

        //     const res1 = await axios.get('http://localhost:5000/interview/my', config)
            
        //     let posts = res1.data
        //     // console.log(posts)
        //     posts.sort((post1, post2) => {
        //         let x = new Date(post1.createdAt)
        //         let y = new Date(post2.createdAt)
        //         if(x<y) return 1
        //         if(x>=y) return -1
        //         return 0
        //     })
        //     // console.log(posts.data.profileId.avatar)
        //     setPosts(posts)
        //     // setSuggestions(posts)

        // } catch (error) {

        //     console.log(error.response)

        // }

    }

    return (
        <>
        <Navbar />
        <MDBContainer className="post pb-4 mt-4 " xl="8">
            {
                posts.length!==0 ?
                posts.map((post) => {
                    return <PostItem  key={post._id} data={post} parent="Interview Experience" onDelete={onDelete}/>
                })
                :
                <Alert severity="info">No interview experiences!!</Alert>
            }
        </MDBContainer>
        </>
    )
}

export default YouInterviewExperiences
