import React, { useEffect, useState } from "react";
import axios from 'axios'
import Avatar from '@material-ui/core/Avatar';
import { MDBContainer,MDBCol,MDBRow, MDBCard, MDBCardFooter, MDBCardBody, MDBCardTitle, MDBCardText, MDBIcon } from "mdbreact";

function UserProfile() {

    const [ myProfile, setMyProfile ] = useState({})

    useEffect(() => {

        const fetchMyProfile = async () => {

            try {

                const config = {
                    headers: {
                        'Content-Type': 'application/json',
                        'auth-token': localStorage.getItem('token')
                    }
                }

                const res = await axios.get(`http://localhost:5000/profile/me`, config)
                console.log(res)
                setMyProfile(res)

            } catch (error) {

                console.log(error.response)

            }

        }

        fetchMyProfile()

    }, [])

    return(
        <MDBCol className="profile pb-4 mt-5" xl="3">
            <MDBCard className="profile_container mdb-color darken-1">
                <MDBRow>
                    <MDBCol className="text-center">
                        <MDBCardBody className="mt-3">
                        {/* /<Avatar /> */}
                            <Avatar src={myProfile.data ? `/images/${myProfile.data.avatar}` : ''} className="red mx-auto" style={{width: '100px', height: '100px'}}>{myProfile.data ? myProfile.data.userId.name[0] : ''}</Avatar>
                            <MDBCardTitle className="text-white font-weight-bold mt-3">{myProfile.data ? myProfile.data.userId.name : ''}</MDBCardTitle>
                            <MDBCardText className="text-white">{myProfile.data ? myProfile.data.company : ''}</MDBCardText>
                            <hr className="hr-text" data-content=""></hr>
                            <MDBRow>
                            <MDBCol className="text-center">
                                {
                                    myProfile.data && myProfile.data.twitter && <a href={myProfile.data.twitter}>
                                    <MDBIcon size="2x" className="text-white pr-2 profile_logo" fab icon="twitter" />
                                    </a>
                                }
                                {
                                    myProfile.data && myProfile.data.instagram && <a href={myProfile.data.instagram}>
                                    <MDBIcon size="2x" className="text-white pr-2 profile_logo" fab icon="instagram" />
                                    </a>
                                }
                                {
                                    myProfile.data && myProfile.data.facebook && <a href={myProfile.data.facebook}>
                                    <MDBIcon size="2x" className="text-white pr-2 profile_logo" fab icon="facebook" />
                                    </a>
                                }
                                {
                                    myProfile.data && myProfile.data.linkedIn && <a href={myProfile.data.linkedIn}>
                                    <MDBIcon size="2x" className="text-white pr-2 profile_logo" fab icon="linkedin" />
                                    </a>
                                }
                            </MDBCol>
                            </MDBRow>
                        </MDBCardBody>
                    </MDBCol>
                </MDBRow>
            </MDBCard>
        </MDBCol>
    )
}

export default UserProfile
